@Transactional
public Order completeOrder(Long orderId) {
    Optional<Order> orderOpt = orderDao.findById(orderId);
    if (!orderOpt.isPresent()) {
        throw new ApiException(HttpStatus.NOT_FOUND, "Order not found with id: "+ orderId);
    }

    Order order = orderOpt.get();
    String currentStatus = order.getOrderStatus();

    if ("CANCELED".equals(currentStatus)) {
        throw new ApiException(HttpStatus.BAD_REQUEST, "Cannot complete a canceled order");
    }

    if ("COMPLETED".equals(currentStatus)) {
        throw new ApiException(HttpStatus.BAD_REQUEST, "Order is already completed");
    }

    if (!"PROCESSING".equals(currentStatus)) {
        throw new ApiException(HttpStatus.BAD_REQUEST, "Only processing orders can be completed");
    }

    // Update order status to COMPLETED
    order.setOrderStatus("COMPLETED");
    return orderDao.update(order);
}

