@Transactional
public List<ProductStatsInfo> getFrequentProductsForUser(Long userId, int limit) {
    if (userId == null || limit <= 0) {
        return Collections.emptyList();
    }

    List<OrderItem> orderItems = orderItemDao.findCompletedItemsByUserId(userId);
    if (orderItems.isEmpty()) {
        return Collections.emptyList();
    }

    Map<Long, Long> quantityByProductId = new HashMap<>();
    Map<Long, Product> productById = new HashMap<>();
    for (OrderItem item : orderItems) {
        if (item == null) {
            continue;
        }
        Product product = item.getProduct();
        if (product == null) {
            continue;
        }
        Long productId = product.getProductId();
        if (productId == null) {
            continue;
        }
        Integer itemQuantity = item.getQuantity();
        if (itemQuantity == null || itemQuantity <= 0) {
            continue;
        }
        quantityByProductId.merge(productId, itemQuantity.longValue(), Long::sum);
        productById.putIfAbsent(productId, product);
    }

    if (quantityByProductId.isEmpty()) {
        return Collections.emptyList();
    }

    List<Map.Entry<Long, Long>> entries = new ArrayList<>(quantityByProductIds.entrySet());
    entries.sort((left, right) -> {
        int compareQuantity = Long.compare(right.getValue(), left.getValue());
        if (compareQuantity != 0) {
            return compareQuantity;
        }
        return Long.compare(left.getKey(), right.getKey());
    });

    List<ProductStatsInfo> frequentProducts = new ArrayList<>(Math.min(limit, entries.size()));
    for (Map.Entry<Long, Long> entry : entries) {
        if (frequentProducts.size() >= limit) {
            break;
        }
        Product product = productById.get(entry.getKey());
        product.setQuantity(quantityByProductId.get(product.getProductId()).intValue());
        if (product != null) {
            frequentProducts.add(new ProductStatsInfo(product, entry.getValue().toString()));
        }
    }

    return frequentProducts;
}

