@Transactional(readOnly = true)
    public Set<Product> getUserWatchlist() {
        Long userId = SecurityUtil.getCurrentUserId();
        if (userId == null) {
            throw new ApiException(HttpStatus.UNAUTHORIZED, "No authenticated user found.");
        }
        boolean isAdmin = SecurityUtil.isAdmin();
        Optional<User> userOpt = userDao.findById(userId);
        if (!userOpt.isPresent()) {
            throw new ApiException(HttpStatus.NOT_FOUND, "User not found with id: " + userId);
        }

        Set<Product> watchlist = userOpt.get().getWatchlist();
        if (watchlist == null || watchlist.isEmpty()) {
            return Collections.emptySet();
        }

        if (!isAdmin) {
            return watchlist;
        }

        Set<Product> inStock = new HashSet<>();
        for (Product product : watchlist) {
            if (product == null) {
                continue;
            }

            Integer quantity = product.getQuantity();
            if (quantity != null && quantity > 0) {
                inStock.add(product);
            }
        }

        return inStock.isEmpty() ? Collections.emptySet() : inStock;
    }

